import React, { useState, useEffect } from 'react'
import styled from 'styled-components'
import FadeIn from 'react-fade-in'
import HangOn from './media/load.gif'
import ReactLoading from 'react-loading'
import useSound from 'use-sound';
import Pop from './media/pop.mp3'
import owl from './media/owl.mp3'
import {Howl, Howler} from 'howler';

const useAudio = url => {
  const [audio] = useState(new Audio(url));
  const [playing, setPlaying] = useState(false);

  const toggle = () => setPlaying(!playing);

  useEffect(() => {
      playing ? audio.play() : audio.pause();
    },
    [playing]
  );

  useEffect(() => {
    audio.addEventListener('ended', () => setPlaying(false));
    return () => {
      audio.removeEventListener('ended', () => setPlaying(false));
    };
  }, []);

  return [playing, toggle];
};



const Loading = () => {
  const [playing, toggle] = useAudio(Pop);

    return (
      <FadeIn >
        <Div  >
          <Containers>
            <H1>Lazy AiOwl</H1>
            <div>
      <button onClick={toggle}>{playing ? "Pause" : "Play"}</button>
    </div>
            <Img  id="img" src={HangOn} />
            <Rloading type={'bubbles'} color={'black'} height={64} width={64} />
            <BtnDiv>
              <BtnA href="#" >
                <BtnP><span className="bg"></span><span className="base"></span><span className="text">Start</span></BtnP>
              </BtnA>
            </BtnDiv>
          </Containers>
        </Div>
      </FadeIn>
    )
  }
  
  export default Loading

  const BtnDiv = styled.div`
  display: flex;
	 align-items: center;
	 justify-content: center;
	 flex-flow: column;
  `

  const BtnA = styled.a`
  width: 100%;
	 max-width: 240px;
	 height: 54px;
	 padding: 8px;
	 font-size: 0.8rem;
	 font-weight: 900;
	 color: #ff4655;
	 text-align: center;
	 text-transform: uppercase;
	 text-decoration: none;
	 box-shadow: 0 0 0 1px inset rgba(0, 0, 0, 0.3);
	 position: relative;
   margin: 10px 0;
   :hover > p {
    color: #ece8e1;
 }
 :hover > p span.text {
  box-shadow: 0 0 0 1px #ece8e1;
}
p {
  background: #00000000;
  color: #000000;
}
p span.base {
  border: 1px solid #ece8e1;
}
:after, :before {
  content: "";
  width: 1px;
  position: absolute;
  height: 8px;
  background: #fff;
  left: 0;
  top: 50%;
  transform: translateY(-50%);
}
:before {
  right: 0;
  left: initial;
}
p span.base {
  box-sizing: border-box;
  position: absolute;
  z-index: 2;
  width: 100%;
  height: 100%;
  left: 0;
  border: 1px solid #000000;
}
p span.base:before {
  content: "";
  width: 2px;
  height: 2px;
  left: -1px;
  top: -1px;
  background: #fff;
  position: absolute;
  transition: 0.3s ease-out all;
}
p span.bg {
  left: -5%;
  position: absolute;
  background: #ff4655;
  width: 0;
  height: 100%;
  z-index: 3;
  transition: 0.3s ease-out all;
  transform: skewX(-10deg);
}
p span.text {
  z-index: 4;
  width: 100%;
  height: 100%;
  position: absolute;
  left: 0;
  top: 0;
}
p span.text:after {
  content: "";
  width: 4px;
  height: 4px;
  right: 0;
  bottom: 0;
  background: #fff;
  position: absolute;
  transition: 0.3s ease-out all;
  z-index: 5;
}
:hover {
  color: #ece8e1;
}
:hover span.bg {
  width: 110%;
}
:hover span.text:after {
  background: #ece8e1;
}
  `

  const BtnP = styled.p`
  margin: 0;
	 height: 54px;
	 line-height: 54px;
	 box-sizing: border-box;
	 z-index: 1;
	 left: 0;
	 width: 100%;
	 position: relative;
	 overflow: hidden;
  `


  const Rloading = styled(ReactLoading)`
  margin:auto;
  `
  const Div = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100vh;
  `
  const H1 = styled.h1`
  text-align: center;
  font-family: "Roboto Condensed", sans-serif;
  animation-name: blinker;
  animation-duration: 1s;
  animation-iteration-count: infinite;
  animation-timing-function: ease-in-out;
  animation-direction: alternate;
  @keyframes blinker {
    from {
      opacity: 1;
    }
    to {
      opacity: 0;
    }
  }
  @media screen and (max-width: 600px) {
    font-size: 1.5rem;
  }
  `
  const Img = styled.img`
  max-width: 100%;
  max-height: 100%;
  margin-right: auto;
  display: block;
  margin-left: auto;
  @media screen and (max-width: 430px) {
    width: 50%;
  }
  `
  const Containers = styled.div`
  position: relative;

  `
