import React, {
  useEffect,
  useState,
  useContext
} from 'react'
import './App.css';
import { AiOwlContext } from './ContextAiOwl'
import aiowl from './media/load.gif'
import aiowli from './media/loadi.gif'
import Loading from './loading'
import FadeIn from 'react-fade-in'
import styled, {ThemeProvider} from 'styled-components'
import  {useDarkMode} from "./useDarkMode"
import Particles from 'react-particles-js';
import { GlobalStyles } from "./GlobalStyles";
import { lightTheme, darkTheme } from "./Theme"
import Toggle from "./Toggler"
import CrossfadeImage from 'react-crossfade-image'

function App() {
  const [loading, setLoading, eel] = useContext(AiOwlContext)
  let [command, setCommand] = useState('dummy command')
  let [title, setTitle] = useState('dummy title')
  let [thumbnail, setThumbnail] = useState('')
  let [result, setResult] = useState('dummy result')
  let [source, setSource] = useState('dummy source')
  let [gogoduck, setGogoduck] = useState({})
  let [musicOn, setMusicOn] = useState(false)
  const [theme, themeToggler] = useDarkMode();

  const themeMode = theme === 'light' ? lightTheme : darkTheme;

  const updateC = (data) => {
    setCommand(data)
  }
  window.eel.expose(updateC, 'updateC')

  const updateTnB = (title, body, tmb) => {
    setTitle(title)
    setResult(body)
    if (tmb) {
      setMusicOn(true)
      } else {
      setMusicOn(false)
      }
  }
  window.eel.expose(updateTnB, 'updateTnB')
const bacc = ()=> {
  setLoading(!loading)
  console.log(loading);
}
  const updateP = (data) => {
    setThumbnail(`http://img.youtube.com/vi/${data}/mqdefault.jpg`)
  }
  window.eel.expose(updateP, 'updateP')

  const gogoDuck = (data) => {
    setGogoduck(data)
    console.log(gogoduck);
  }
  window.eel.expose(gogoDuck, 'gogoDuck')

  const pari = {
    "particles": {
        "number": {
            "value": 60,
            "density": {
                "enable": true,
                "value_area": 1500
            }
        },
        "line_linked": {
            "enable": true,
            "opacity": 0.02
        },
        "move": {
            "direction": "right",
            "speed": 0.05
        },
        "size": {
            "value": 1
        },
        "opacity": {
            "anim": {
                "enable": true,
                "speed": 1,
                "opacity_min": 0.05
            }
        }
    },
    "backgroundMode": {
      "enable": true,
      "zIndex": -1
    },
    "interactivity": {
        "events": {
            "onclick": {
                "enable": true,
                "mode": "push"
            }
        },
        "modes": {
            "push": {
                "particles_nb": 1
            }
        }
    },
    "retina_detect": true
}

  return (
<ThemeProvider theme={themeMode}>
      <>
      <GlobalStyles/>
      <Particles
    params={pari} />
    loading ? <Loading /> :
    <Div id="otosan" className="papi">
    <FadeIn>
        <div id="dots" />
        <div className='topdiv'>
        <CrossfadeImage src={theme === 'light' ? aiowl : aiowli} duration='460' timingFunction='linear' />
        </div>
        <div className="middiv">
          <h2 id="command">{command}</h2>
          <h1 id="resultTitle">{title}</h1>
          {musicOn ? 
          <div className="dd">
            <img id="resultimg" src={thumbnail} alt="" />
          </div> : <div />}
          <p id="resultBody">{result}</p>
          <p id="resultsrc">{source}</p>
          <Toggle theme={theme} toggleTheme={themeToggler} />
            <button onClick={bacc} >go load</button>
        </div>
        </FadeIn>
      </Div> 
      </>
    </ThemeProvider>
  );
}

export default App;

const Div = styled.div`
`
