import React from 'react'
import styled from "styled-components"
import switchB from './media/switch.png'
import switchBi from './media/switchi.png'
import CrossfadeImage from 'react-crossfade-image'
const CrossfadeImageS = styled(CrossfadeImage)`
max-width:20% !important;
max-height:20% !important;
img {
    max-width:20% !important;
max-height:20% !important;
}
`
const Toggle = ({theme,  toggleTheme}) => {
    return (
     <CrossfadeImageS onClick={toggleTheme} src={theme === 'light' ? switchBi : switchB} duration='460' timingFunction='linear' />
    );
};

export default Toggle
