import React from 'react'
import styled from "styled-components"
import switchB from './media/switch.png'
import switchBi from './media/switchi.png'
import CrossfadeImage from 'react-crossfade-image'
import DayNightToggle from 'react-day-night-button'


const Toggle = ({theme,  toggleTheme}) => {
    return (
<DayNightToggle 
        onChange={booleanValue => console.log(booleanValue)} 
        defaultsNight={true}
        className={"additional-class"}
    />
        // <Dwiiv onClick={toggleTheme}>
        //     <CrossfadeImage 
        //     src={theme === 'light' ? switchBi : switchB} 
        //     duration='460' timingFunction='linear' />
        // </Dwiiv>
    );
};

export default Toggle

const Dwiiv = styled.div`
width: 100px;
position: absolute;
cursor: pointer;
:hover {
    transform: scale(1.1);
    transition: transform 0.5s ease-in-out;
}
`
