import React from 'react'
import styled from "styled-components"
import switchB from './media/switch.png'
import switchBi from './media/switchi.png'
import CrossfadeImage from 'react-crossfade-image'
import './toggler.css'
let checked 
const Toggle = ({theme,  toggleTheme}) => {
 if (theme == 'light') {
   checked = false
 } else {
   checked = true
 }
    return (
<div class="toggleWrapper">
  <input type="checkbox" defaultChecked={checked} className="dn" id="dn" onChange={toggleTheme}/>
  <label htmlFor="dn" class="toggle">
    <span className="toggle__handler">
      <span className="crater crater--1"></span>
      <span className="crater crater--2"></span>
      <span className="crater crater--3"></span>
    </span>
    <span className="star star--1"></span>
    <span className="star star--2"></span>
    <span className="star star--3"></span>
    <span className="star star--4"></span>
    <span className="star star--5"></span>
    <span className="star star--6"></span>
  </label>
</div>
        // <Dwiiv onClick={toggleTheme}>
        //     <CrossfadeImage 
        //     src={theme === 'light' ? switchBi : switchB} 
        //     duration='460' timingFunction='linear' />
        // </Dwiiv>
    );
};

export default Toggle

const Dwiiv = styled.div`
width: 100px;
position: absolute;
cursor: pointer;
:hover {
    transform: scale(1.1);
    transition: transform 0.5s ease-in-out;
}
`
