import React, {
  useState,
  useContext
} from 'react'
import './App.css';
import { AiOwlContext } from './ContextAiOwl'
import aiowl from './media/load.gif'
import aiowli from './media/loadi.gif'
import Loading from './loading'
import FadeIn from 'react-fade-in'
import styled, {ThemeProvider} from 'styled-components'
import  {useDarkMode} from "./useDarkMode"
import Particles from 'react-particles-js'
import { GlobalStyles } from "./GlobalStyles"
import { lightTheme, darkTheme } from "./Theme"
import Toggle from "./Toggler"
import CrossfadeImage from 'react-crossfade-image'
import Clock from 'react-live-clock';
import TextTransition, { presets } from "react-text-transition";
import ReactRoundedImage from "react-rounded-image";
import { Wave, Random } from 'react-animated-text';


function App() {
  const [loading, setLoading, eel ,pari] = useContext(AiOwlContext)
  let [command, setCommand] = useState('')
  let [title, setTitle] = useState('')
  let [thumbnail, setThumbnail] = useState('')
  let [result, setResult] = useState('')
  let [source, setSource] = useState('')
  let [gogoduck, setGogoduck] = useState({})
  let [musicOn, setMusicOn] = useState(false)
  let [theme, themeToggler] = useDarkMode()
  let [dateShow, SetShowDate] = useState(false)
  const themeMode = theme === 'light' ? lightTheme : darkTheme;
  let [nmbr, setNmbr] = useState({ paused: true, randomNumber: result })

  const updateC = (data) => {
    setCommand(data)
  }
  window.eel.expose(updateC, 'updateC')

  const updateTnB = (title, body, tmb) => {
    setTitle(title)
    setResult(body)
    setNmbr({ paused: !nmbr.paused, randomNumber: body })
    if (tmb) {
      setMusicOn(true)
      } else {
      setMusicOn(false)
      }
  }
  window.eel.expose(updateTnB, 'updateTnB')

  const updateP = (data) => {
    setThumbnail(`http://img.youtube.com/vi/${data}/mqdefault.jpg`)
  }
  window.eel.expose(updateP, 'updateP')

  const gogoDuck = (data) => {
    setGogoduck(data)
    console.log(gogoduck);
  }
  window.eel.expose(gogoDuck, 'gogoDuck')

  const showDate = () => SetShowDate(true)
  const hideDate = () => SetShowDate(false)

  const dateu = dateShow ? <Clock format={'dddd, MMM D'} ticking={true} timezone={'Africa/Casablanca'} /> : ''
  const yearu = dateShow ? <Clock format={'YYYY'} ticking={true} timezone={'Africa/Casablanca'} /> : ''

  return (loading ? <Loading /> :
    <ThemeProvider theme={themeMode}>
      <>
        <GlobalStyles />
        <Particles params={pari} />
        <Div id="otosan" className="papi">
          <FadeIn delay={500}>
          <Toggle theme={theme} toggleTheme={themeToggler} />
            <TopBar onMouseOver={showDate} onMouseLeave={hideDate}>
              <Date>
              <TextTransition direction='down' text={dateu} springConfig={ presets.gentle }/>
              </Date> 
              <Clock format={'hh:mm A'} ticking={true} timezone={'Africa/Casablanca'} />
              <AmPm>
              <TextTransition direction='down' text={yearu} springConfig={ presets.gentle }/>
              </AmPm>
            </TopBar>
            <div className='topdiv'>
              <CrossfadeImage src={theme === 'light' ? aiowl : aiowli} duration='460' timingFunction='linear' />
            </div>
            <div className="middiv">
            <TextTransition direction='down' style={{fontWeight: '800',marginBottom: '25px', color: 'purple', fontSize:'26px'}} text={command} springConfig={ presets.wobbly }/>
            <TextTransition direction='up' style={{fontWeight: '800',marginBottom: '25px', color: '#1abe7f', fontSize:'22px'}} text={title} springConfig={ presets.gentle }/>
            <TextTransition direction='up' style={{fontSize:'15px', marginBottom: '25px'}} text={result} springConfig={ presets.gentle }/>
            <Random
          text={nmbr.result}
          paused={nmbr.paused}
          iterations={1}
          effect="verticalFadeIn"
          effectChange={2}
          effectDirection="up"
        />
              {/* <h2 id="command">{command}</h2> */}
              {/* <h1 id="resultTitle">{title}</h1>
              <p id="resultBody">{result}</p> */}
              {musicOn ? 
              <ImgDiv>
                <ReactRoundedImage
                  image={thumbnail}
                  roundedColor={theme === 'light' ? 'black' : 'white'}
                  imageWidth="150"
                  imageHeight="150"
                  roundedSize="3"
                  hoverColor="#DD1144"
                />
              </ImgDiv>: <div />}
              <p id="resultsrc">{source}</p>
            </div>
          </FadeIn>
        </Div> 
      </>
    </ThemeProvider>
  )
}

export default App;

const ImgDiv = styled.div`
display: flex;
align-items: center;
justify-content: center;
img {
  object-fit: cover !important;
}`

const TopBar = styled.div`
// border: 1px solid black;
display:flex; 
text-align: center;
justify-content: center;
margin-bottom: 25px;
cursor: default;
time {
  width: 20%;
}
`
const Date = styled.span`
text-align: right;
width: 30%;
`
const AmPm = styled.span`
width: 30%;
text-align: left;

`
const Div = styled.div`
`
