import React from 'react'
import styled from "styled-components"
import switchB from './media/switch.png'
import switchBi from './media/switchi.png'
import CrossfadeImage from 'react-crossfade-image'

const Toggle = ({theme,  toggleTheme}) => {
    const fricc = () => {
        console.log('fricc')
    }
    return (
        <div onClick={toggleTheme}>
            <CrossfadeImage style={{ maxWidth: '30%', maxHeight: '30%'}} 
            onClick={fricc} src={theme === 'light' ? switchBi : switchB} 
            duration='460' timingFunction='linear' />
        </div>
    );
};

export default Toggle
