import React from 'react'
import styled from "styled-components"
import switchB from './media/switch.png'
import switchBi from './media/switchi.png'
import CrossfadeImage from 'react-crossfade-image'
import './toggler.css'

const Toggle = ({theme,  toggleTheme}) => {
    return (
<div class="toggleWrapper">
  <input type="checkbox" class="dn" id="dn" onChange={toggleTheme}/>
  <label for="dn" class="toggle">
    <span class="toggle__handler">
      <span class="crater crater--1"></span>
      <span class="crater crater--2"></span>
      <span class="crater crater--3"></span>
    </span>
    <span class="star star--1"></span>
    <span class="star star--2"></span>
    <span class="star star--3"></span>
    <span class="star star--4"></span>
    <span class="star star--5"></span>
    <span class="star star--6"></span>
  </label>
</div>
        // <Dwiiv onClick={toggleTheme}>
        //     <CrossfadeImage 
        //     src={theme === 'light' ? switchBi : switchB} 
        //     duration='460' timingFunction='linear' />
        // </Dwiiv>
    );
};

export default Toggle

const Dwiiv = styled.div`
width: 100px;
position: absolute;
cursor: pointer;
:hover {
    transform: scale(1.1);
    transition: transform 0.5s ease-in-out;
}
`
